module.exports = function (pkg, content, file) {
    var indented;
    var indent = '';
    var lines = [];

    while (indent.length < pkg.indent) {
        indent += ' ';
    }

    if ((/^js\//).test(file)) {
        console.log("Indenting '" + file + "'");

        content
            .split('\n')
            .forEach(function (line) {
                if (line.replace(' ', '').replace('\n', '').length > 0) {
                    lines.push(indent + line);
                } else {
                    lines.push('');
                }
            });

        indented = lines.join('\n');
    }

    return (indented || content);
};